x86:instruction.pre:
C1 ← sign bit of ST; (* 0 for positive, 1 for negative *)
CASE (class of value or number in ST(0)) OF
    Unsupported:C3, C2, C0 ← 000;
    NaN:
        C3, C2, C0 ← 001;
    Normal:
        C3, C2, C0 ← 010;
    Infinity:
        C3, C2, C0 ← 011;
    Zero:
        C3, C2, C0 ← 100;
    Empty:
        C3, C2, C0 ← 101;
    Denormal:
        C3, C2, C0 ← 110;
ESAC;
